"use strict"

const { checkPublish, messages } = require("eslint-plugin-n/lib/util/check-publish")
const getAllowModules = require("eslint-plugin-n/lib/util/get-allow-modules")
const getConvertPath = require("eslint-plugin-n/lib/util/get-convert-path")
const getResolvePaths = require("eslint-plugin-n/lib/util/get-resolve-paths")
const getTryExtensions = require("eslint-plugin-n/lib/util/get-try-extensions")
const visitRequire = require("eslint-plugin-n/lib/util/visit-require")

module.exports = {
    meta: {
        docs: {
            description:
                "disallow `require()` expressions which import private modules",
            category: "Possible Errors",
            recommended: true,
            url: "https://github.com/weiran-zsd/eslint-plugin-node/blob/HEAD/docs/rules/no-unpublished-require.md",
        },
        type: "problem",
        fixable: null,
        schema: [
            {
                type: "object",
                properties: {
                    allowModules: getAllowModules.schema,
                    convertPath: getConvertPath.schema,
                    resolvePaths: getResolvePaths.schema,
                    tryExtensions: getTryExtensions.schema,
                },
                additionalProperties: false,
            },
        ],
        messages,
    },
    create(context) {
        const filePath = context.getFilename()
        if (filePath === "<input>") {
            return {}
        }

        return visitRequire(context, {}, targets => {
            checkPublish(context, filePath, targets)
        })
    },
}
